import Vuex from 'vuex'

// store存储
// 现在我们的 cartList 就是一个对象，里面没有存储任何信息，
//因为只有用户在购物车里进行操作了，才会有数据的存储或改变。
//当用户点击加号时，先判断 cartList 里有没有存储商铺的信息，
//没有的话就存储一个空对象，再判断商铺里面有没有选择的商品的信息，
//没有的话就把传过来的 prodectInfo 进行赋值，再添加一个 count 属性用来统计数量。
//因为我们现在是增加的操作，所以最后一定得让数量加一，最后再把改变后的数据存储在 state 中。
export default Vuex.createStore({
  state: {
    cartList: {
      // 第一层级是商铺的id
      // shopId: {
        // 第二层是商品id
        // 第二层内容是商品内容以及购物数量
      //   productId: {
      //     _id: '1',
      //     name: '番茄250g/份',
      //     imgUrl: 'http://www.dell-lee.com/imgs/vue3/tomato.png',
      //     sales: 10,
      //     price: 33.6,
      //     oldPrice: 39.6,
      //     count: 2
      //   },
      // },
    }
  },
  mutations: {
    changeCartItemInfo(state, payload) {
      const { shopId, productId, productInfo } = payload;
      let shopInfo = state.cartList[shopId]
      if(!shopInfo) { shopInfo = {} }
      let product = shopInfo[productId]
      if(!product) {
        product = productInfo
        product.count = 0
      }
      product.count = product.count + payload.num
      if(product.count < 0) { product.count = 0 }
      shopInfo[productId] = product
      state.cartList[shopId] = shopInfo
    }
  },
  actions: {
  },
  modules: {
  }
})
